<?php

namespace airdrops\entity;

use airdrops\AirDrops;
use airdrops\entity\types\FinalNPC;
use airdrops\entity\types\NPC;
use airdrops\scheduler\EntityDisappearScheduler;
use pocketmine\data\bedrock\EffectIdMap;
use pocketmine\data\bedrock\EffectIds;
use pocketmine\entity\effect\EffectInstance;
use pocketmine\entity\effect\VanillaEffects;
use pocketmine\entity\EntityDataHelper;
use pocketmine\entity\Human;
use pocketmine\entity\Location;
use pocketmine\entity\Skin;
use pocketmine\nbt\tag\CompoundTag;
use pocketmine\player\Player;
use pocketmine\Server;
use pocketmine\utils\SingletonTrait;
use pocketmine\utils\TextFormat;
use pocketmine\world\World;

class EntityFactory {
    use SingletonTrait;

    public function start(): void {
        \pocketmine\entity\EntityFactory::getInstance()->register(NPC::class, function (World $world, CompoundTag $compoundTag): NPC {
            return new NPC(EntityDataHelper::parseLocation($compoundTag, $world), Human::parseSkinNBT($compoundTag), $compoundTag);
        }, ["npc:airdrops"]);
        \pocketmine\entity\EntityFactory::getInstance()->register(FinalNPC::class, function (World $world, CompoundTag $compoundTag): FinalNPC {
            return new FinalNPC(EntityDataHelper::parseLocation($compoundTag, $world), Human::parseSkinNBT($compoundTag), $compoundTag);
        }, ["npc:chest"]);
    }

    public function create(Location $player, string $id_name, Skin $skin): void {
        $human = new NPC($player, $skin);
        foreach (Server::getInstance()->getWorldManager()->getWorlds() as $worlds) {
            foreach ($worlds->getEntities() as $entities) {
                if ($entities instanceof NPC) {
                    if ($entities->equals($id_name)) {
                        $this->eliminateByName($id_name);
                    }
                }
            }
        }
        $human->setIdName($id_name);
        $human->setNametagVisible();
        $human->setNameTagAlwaysVisible();
        $human->spawnToAll();
        $human->setHasGravity(false);
        $human->setImmobile();
        AirDrops::getInstance()->getScheduler()->scheduleRepeatingTask(new EntityDisappearScheduler($human), 20);
    }

    public function createChest(Location $player, string $id_name, Skin $skin): void {
        $human = new FinalNPC($player, $skin);
        foreach (Server::getInstance()->getWorldManager()->getWorlds() as $worlds) {
            foreach ($worlds->getEntities() as $entities) {
                if ($entities instanceof NPC) {
                    if ($entities->equals($id_name)) {
                        $this->eliminateByName($id_name);
                    }
                }
            }
        }
        $human->setIdName($id_name);
        $human->setNameTag(TextFormat::colorize("&l&6AirDrop"));
        $human->setNametagVisible();
        $human->setNameTagAlwaysVisible();
        $human->sendSkin(Server::getInstance()->getOnlinePlayers());
        $human->spawnToAll();
        $human->setHasGravity(false);
        $human->setImmobile();
    }

    public function getInvisibleSkin(): bool|string {
        return base64_decode("");
    }

    public function eliminateByName(string $id_name): void {
        foreach (Server::getInstance()->getWorldManager()->getWorlds() as $world) {
            foreach ($world->getEntities() as $entities) {
                if ($entities instanceof NPC or $entities instanceof FinalNPC) {
                    if ($entities->equals($id_name)) {
                        $entities->close();
                    }
                }
            }
        }
    }
}